import React, { Component } from 'react';
import '../../App.css';
import './Listen.css';
import rightSupremacy from '../img/RightSupremacy.jpg';
import agentOrange from '../img/AgentOrange.jpg';

class Listen extends React.Component {
	state = {
    showBox: false
  	};

  	/* Showbox will have a hover event for each img.  When you hover, links will appear
  	   that will go to the streaming and listening sites for each album. */

  	handleBoxToggle = () => this.setState({ showBox: !this.state.showBox });

	render () {
		return (
			<div className="listenPage">
			        <div 
			        onMouseEnter={this.handleBoxToggle}
			        className={`wrapper${this.state.showBox ? " show" : ""}`}>
			          <div className="innerBox" />
			        </div>
				<img className="listenImages" id='rightSupremacy' src={rightSupremacy} alt="Right Supremacy Art" />
				<img className="listenImages" id='agentOrange' src={agentOrange} alt="Agent Orange Art" />
	  		</div>
		);
	}
}

export default Listen;
